const iconComponentMap = {
  codepen: CodepenIcon,
  linkedin: LinkedInIcon,
  github: GithubIcon,
  imgur: ImgurIcon
}

function IconLink(props) {
  return (
    <a 
      className={`iconLink ${props.icon}-link`}
      href={props.url}
      target="_blank"
      rel="noreferrer">
      {iconComponentMap[props.icon] && iconComponentMap[props.icon]()}
    </a>
  )
}

export default IconLink